import logo from './logo.svg';
import './App.css';
import * as d3 from 'd3';
import React, { useRef, useState, useLayoutEffect, useEffect } from 'react';

function App() {
  const containerRef = React.useRef(null);
  const [data, setValue] = useState([1,2,3]);
  const [width, setWidth] = useState(0);

  useEffect(() => {
    function updateWidth() {
      setWidth(containerRef.current.clientWidth / (data.length + 1));
    }
    const handleResize = d3.debounce(updateWidth, 500);
    updateWidth();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [data.length]);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const update = d3.select('g').selectAll('circle').data(data);

      update
          .enter()
          .append('circle')
          .attr('r', (d) => d)
          .attr('cx', (_, i) => width * (i + 1))
          .attr('cy', () => Math.random() * 100)
          .attr('stroke', (_, i) => (i % 2 === 0 ? '#f80' : '#aaf'))
          .attr('fill', (_, i) => (i % 2 === 0 ? 'orange' : '#44f'));

      update
          .attr('r', (d) => d)
          .attr('cx', (_, i) => width * (i + 1))
          .attr('cy', () => Math.random() * 100)
          .attr('stroke', (_, i) => (i % 2 === 0 ? '#f80' : '#aaf'))
          .attr('fill', (_, i) => (i % 2 === 0 ? 'orange' : '#44f'));

      update.exit().remove();
    }
  }, [data]);



  return (
    <div className="App">
      <header className="">

        <div>{data}</div>
        <svg id="content" width={width} height="100%" ref={containerRef}>
          <g transform="translate(0, 100)" />

        </svg>
      </header>
    </div>
  );
}

export default App;
