import React, { useRef, useState, useLayoutEffect } from 'react';
import { select } from 'd3';
import { getRandomColor } from "./utils/randomColors";

function ListOfElements(){
    let i = 0;
    function Element(){
        let obj = {
            id: i,
            value: Math.floor(Math.random() * 100),
            color: getRandomColor()
        };
        i++;
        console.log(i);
        return obj;
    }
    return Element;
}

function App() {
  const svgWidth = 1000;
  const svgHeight = 300;
  let element = ListOfElements();
  const [data, setData] = useState([
        element(), element()//, element(), element(), element(), element()
      ]);
  const containerRef = useRef(null);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const histogram = select('svg').selectAll('rect').data(data);
    console.log("Array:", data);
      histogram
          .enter()
          .append('rect')
          .merge(histogram)
          .attr('x', (_, i) => i * svgWidth/(data.length+1) + (100/data.length))
          .attr('transform', d => `translate(0,${svgHeight-(d.value * 300 / svgHeight)})`) // per allinearli tutti verso il basso
          .attr("width", (svgWidth-100) / (data.length + 1))
          .attr("height", d => d.value * 300 / svgHeight)
          .attr('fill', d => d.color)
          .on('mouseenter', function (actual, i) {
              select(this).attr('opacity', 0.6)
          })
          .on('mouseleave', function (actual, i) {
              select(this).attr('opacity', 1)
          })
          .on('click', (actual, data) => {

              var response = prompt("Change value:", data.value);
              if (response == null || response == "") {
                  alert("You cancelled the prompt.");
                  return;
              }
              if(!isNaN(response)){
                  let value = parseFloat(response);
                  setData(oldData => {
                      console.log("Quaa", oldData);
                      oldData[data.id] = {
                          ...oldData[data.id],
                          value
                      };
                      /*
                      console.log(" Data:", data);
                      console.log("New element:", oldData[data.id]);*/
                      return [...oldData];
                  })
                  return;
              }
              alert("Something went wrong!");
          })
      histogram.exit().remove();
    }
  }, [data]);

  return (
      <>

        <svg width={svgWidth} height={svgHeight} ref={containerRef}>

        </svg>
        <button
          onClick={() => {
            setData(d => [...d, element()])
          }}>
            ciaoo
        </button>
        <div>
          {data.map(i => i.value + ", ")}</div>
      </>
  );
};

export default App;
