                    return `translate(${i*translateX},${y(d.value)})`; })
                .attr("width", function(d) {
                    return x(d.value)-20  ; })
                .attr('value', d => d.value)
                .attr("height", function(d) { return height - y(d.value); })
                .style("fill", d => d.color)


