function getRandomColor(shade = 'light'){

    let getColor = (digits) => {
        digits = digits.split('');
        let color = '#';
        for(let i = 0; i < 6; i++){
            color += letters[Math.floor(Math.random() * 16) % letters.length];
        }
        return color;
    }

    switch (shade){
        case 'light':
            return getColor('9ABCDEF');
        case 'dark':
            return getColor('012345678');
        default:
            return getColor('0123456789ABCDEF');
    }

    return getRandomLightColor();
}



function getRandomLightColor(){
    const letters = '9,A,B,C,D,E,F'.split(',');
    let color = '#';
    for(let i = 0; i < 6; i++){
        color += letters[Math.floor(Math.random() * 16) % letters.length];
    }
    return color;
}

function setRandomColor(element){
    element.setBackgroundColor(getRandomColor());
}

export {getRandomColor, setRandomColor};
