import logo from './logo.svg';
import './App.css';

import React, { useRef, useState, useLayoutEffect, useEffect } from 'react';
import debounce from 'lodash/debounce';
import * as d3 from 'd3';
import { select } from 'd3';
import { getRandomColor } from "./utils/randomColors";

function App() {
  const [data, setData] = useState([]);
  const [width, setWidth] = useState(0);
  const containerRef = useRef(null);

  useEffect(() => {
    function updateWidth() {
      setWidth(containerRef.current.clientWidth / (data.length + 1));
    }
    const handleResize = debounce(updateWidth, 500);
    updateWidth();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [data.length]);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const update = select('g').selectAll('circle').data(data);

      update
          .enter()
          .append('circle')
          .merge(update)
          .attr('r', (d) => d)
          .attr('cx', (_, i) => width * (i + 1))
          .attr('cy', () => Math.random() * 100)
          .attr('stroke', () => getRandomColor())
          .attr('fill', () => getRandomColor())
          .text(i);

      update.exit().remove();
    }
  }, [data, width]);

  return (
      <>

        <svg width="100%" height="350" ref={containerRef}>
          <g transform="translate(0, 100)" />
        </svg>
        <button
          onClick={() => setData(d => [...d, Math.random()*(Math.random()*30)])}>
ciaoo
        </button>
        <div>
          {data}</div>
      </>
  );
};

export default App;
