import React, { useRef, useState, useLayoutEffect } from 'react';
import { select } from 'd3';
import { getRandomColor } from "./utils/randomColors";

function App() {
  const svgWidth = 1000;
  const svgHeight = 300;
  const [data, setData] = useState([
      {value: 80, color: getRandomColor()},
      {value: 110, color: getRandomColor()},
      {value: 140, color: getRandomColor()},
      {value: 200, color: getRandomColor()},
      {value: 140, color: getRandomColor()},
      {value: 110, color: getRandomColor()},
      {value: 80, color: getRandomColor()}
      ]);
  const containerRef = useRef(null);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const histogram = select('svg').selectAll('rect').data(data);

      histogram
          .enter()
          .append('rect')
          .merge(histogram)
          .attr('x', (_, i) => i * svgWidth/(data.length+1) + (100/data.length))
          .attr('transform', d => `translate(0,${svgHeight-(d.value * 300 / svgHeight)})`) // per allinearli tutti verso il basso
          .attr("width", (svgWidth-100) / (data.length + 1))
          .attr("height", d => d.value * 300 / svgHeight)
          .attr('fill', d => d.color)
          .on('mouseenter', function (actual, i) {
              select(this).attr('opacity', 0.6)
          })
          .on('mouseleave', function (actual, i) {
              select(this).attr('opacity', 1)
          })
          .on('click', (actual, data) => {
              alert("hai cliccato il numero" + data.value + " con il colore " + data.color)
          })
      histogram.exit().remove();
    }
  }, [data]);

  return (
      <>

        <svg width={svgWidth} height={svgHeight} ref={containerRef}>

        </svg>
        <button
          onClick={() => {
            setData(d => [...d, {
              value: Math.floor(Math.random() * 100),
              color: getRandomColor()
            }])
          }}>
            ciaoo
        </button>
        <div>
          {data.map(i => i.value + ", ")}</div>
      </>
  );
};

export default App;
