import logo from './logo.svg';
import './App.css';

import React, { useRef, useState, useLayoutEffect, useEffect } from 'react';
import debounce from 'lodash/debounce';
import * as d3 from 'd3';
import { select } from 'd3';
import { getRandomColor } from "./utils/randomColors";

function App() {
  const svgWidth = 1000;
  const svgHeight = 300;
  const [data, setData] = useState([
      {value: 80, color: getRandomColor()},
      {value: 110, color: getRandomColor()},
      {value: 140, color: getRandomColor()},
      {value: 200, color: getRandomColor()},
      {value: 140, color: getRandomColor()},
      {value: 110, color: getRandomColor()},
      {value: 80, color: getRandomColor()}
      ]);
  const [width, setWidth] = useState(svgWidth);
  const containerRef = useRef(null);

  useEffect(() => {
    function updateWidth() {
      setWidth(containerRef.current.clientWidth / (data.length + 1));
    }
    const handleResize = debounce(updateWidth, 500);
    updateWidth();
    window.addEventListener('resize', handleResize);
    console.log("resizing");
    return () => window.removeEventListener('resize', handleResize);
  }, [data.length]);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const histogram = select('svg').selectAll('rect').data(data);

      histogram
          .enter()
          .append('rect')
          .merge(histogram)
          .attr('x', (_, i) => i * width/(data.length+1) + (100/data.length))
          .attr('transform', d => `translate(0,${svgHeight-(d.value * 300 / svgHeight)})`) // per allinearli tutti verso il basso
          .attr("width", (width-100) / (data.length + 1))
          .attr("height", d => d.value * 300 / svgHeight)
          .attr('fill', d => d.color)
          .on('mouseenter', function (actual, i) {
              select(this).attr('opacity', 0.6).append('text').text("ciao").enter()
          })
          .on('mouseleave', function (actual, i) {
        select(this).attr('opacity', 1)
    })
      console.log(data);
      histogram.exit().remove();
    }
  }, [data, width]);

  return (
      <>

        <svg width={svgWidth} height={svgHeight} ref={containerRef}>

        </svg>
        <button
          onClick={() => {
            setData(d => [...d, {
              value: Math.floor(Math.random() * 100),
              color: getRandomColor()
            }])
          }}>
            ciaoo
        </button>
        <div>
          {data.map(i => i.value + ", ")}</div>
      </>
  );
};

export default App;
