import logo from './logo.svg';
import './App.css';

import React, { useRef, useState, useLayoutEffect, useEffect } from 'react';
import debounce from 'lodash/debounce';
import * as d3 from 'd3';
import { select } from 'd3';
import { getRandomColor } from "./utils/randomColors";

function App() {
  const svgWidth = 500;
  const svgHeight = 300;
  const [data, setData] = useState([20, 10, 30]);
  const [width, setWidth] = useState(svgWidth);
  const containerRef = useRef(null);

  useEffect(() => {
    function updateWidth() {
      //setWidth(containerRef.current.clientWidth / (data.length + 1));
    }
    const handleResize = debounce(updateWidth, 500);
    updateWidth();
    window.addEventListener('resize', handleResize);
    console.log("resizing");
    return () => window.removeEventListener('resize', handleResize);
  }, [data.length]);

  useLayoutEffect(() => {
    if (Array.isArray(data)) {
      const histogram = select('svg').selectAll('rect').data(data);

      histogram
          .enter()
          .append('rect')
          .merge(histogram)
          .attr('x', (_, i) => i * width/(data.length+1) + (100/data.length))
          .attr('transform', d => `translate(0,${100-d*2})`) // per allinearli tutti verso il basso
          .attr("width", (width-100) / (data.length + 1))
          .attr("height", d => d*2)
          .attr('fill', () => getRandomColor());

      console.log(data);
      histogram.exit().remove();
    }
  }, [data, width]);

  return (
      <>

        <svg width="50%" height={svgHeight} ref={containerRef}>

        </svg>
        <button
          onClick={() => {
            setData(d => [...d, Math.floor(Math.random() * 30)])
            console.log(data)
          }}>
            ciaoo
        </button>
        <div>
          {data.map(i => i + ", ")}</div>
      </>
  );
};

export default App;
