            uuid,
            value: Math.floor(Math.random() * 100 + 20), // +20 per evitare la presenza di zeri
            color: getRandomColor()
        };
}

function App() {
  const svgWidth = 1000;
  const svgHeight = 300;

  const [dataMap, setData] = useState(new Map());
  const containerRef = useRef(null);
  let sizeOfData = dataMap.size;


