                  setTimeout(() => {
                      setData(oldMap => {
                          let newMap = new Map(oldMap);
                          newMap.set(data.uuid, {
                              uuid: data.uuid,
                              color: data.color,
                              value: value
                          })
                          // code to modify the slot


                          // gestire gli id
                          return newMap;
                      })
