                      setData(oldMap => {
                          let newMap = new Map(oldMap);
                          newMap.set(data.uuid, {
                              uuid: data.uuid,
                              color: data.color,
                              value: value
                          })
                          // code to modify the slot


                          // gestire gli id
                          return newMap;
                      })
                  }, duration);

return;
              }
              alert("Something went wrong!");
          })
      histogram.exit().remove();
    }
  }, [dataMap]);

  return (
      <>

        <svg width={svgWidth} height={svgHeight} ref={containerRef} />

        <button
          onClick={() => {
            setData(d => {
                let newMap = new Map(d);
                let id = uuidv4();
                newMap.set(id, element(id));
                return newMap;
            })
          }}>
            ciaoo
        </button>
        <div>
            {[...dataMap.keys()].map(i => <div>{i}</div>)}</div>
      </>
  );
};

export default App;
